css:
@media (min-width: 1200px) {
    .atable .container {
        width: 970px;
    }
}

.atable .print-heading h2 {
    font-size: 20px;
    font-family: Calibri;
    color: #00a4ad;
}

.atable .letter-block {
    margin-bottom: 30px;
}

.atable .letter-block p {
    margin: 0 0 2px;
    font-size: 11px;
}

.atable .col-xs-1,
.atable .col-sm-1,
.atable .col-md-1,
.atable .col-lg-1,
.atable .col-xs-2,
.atable .col-sm-2,
