.atable .col-xs-4,
.atable .col-sm-4,
.atable .col-md-4,
.atable .col-lg-4,
.atable .col-xs-5,
.atable .col-sm-5,
.atable .col-md-5,
.atable .col-lg-5,
.atable .col-xs-6,
.atable .col-sm-6,
.atable .col-md-6,
.atable .col-lg-6,
.atable .col-xs-7,
.atable .col-sm-7,
.atable .col-md-7,
.atable .col-lg-7,
.atable .col-xs-8,
