.atable .col-lg-11,
.atable .col-xs-12,
.atable .col-sm-12,
.atable .col-md-12,
.atable .col-lg-12 {
    padding-right: 0px;
    padding-left: 0px;
}

table,
td {
    border-collapse: collapse;
    border-color: #95969A !important;
    font-size: 13px;
}

th {
    border-collapse: collapse;
    border-color: #95969A !important;
    font-size: 13px;
    color: #fff;
}

.atable .row {
    margin-right: 0px;
    margin-left: 0px;
}

.print-format th {
    background-color: #00a4ad!important;
    font-weight: 400;
}

.atable .col-print-4 {
    width: 33.3333333%;
    float: left;
}

.atable .pulled {
    margin-top: -41px !important;
}

.atable .col-print-offset-8 {
    float: right;
    width: 33.33333333%;
}

.atable .col-print-offset-6 {
    float: right;
    width: 50%;
}

.atable .bordered {
    border-top: 1px solid #00a4ad;
    font-style: italic;
    font-size: 12px;
    color: #00a4ad;
}
.print-format {  
    padding: 0.3in;
}
